package day5;

public class increment_decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int i = 10;
     System.out.println("The score is "+ i);
     
     //i = i+1;
     //i += 1;
     //i++; 
     //++i;// preincrement
     System.out.println("The new score is "+ i++);//postincrement
     i--;
     System.out.println("The newest score is "+ i);
	}

}
